import { Fragment, useCallback, useEffect, useMemo, useState } from "react";

const SECONDS_DEFAULT = 10;

export const Home = () => {
  const { tasks, getAllTodos, createTodo, updateTodo } = useTodo();
  const [taskName, setTaskName] = useState("");
  const [seconds, setSeconds] = useState(SECONDS_DEFAULT);
  const [timer, setTimer] = useState<any>();
  const [stage, setStage] = useState("ready");
  const [taskIndex, setTaskIndex] = useState(0);

  const handleOKButton = useCallback(async () => {
    await createTodo({ task: taskName, isDone: 0 });
    await getAllTodos();
    setTaskName('');
  }, [createTodo, getAllTodos, taskName]);

import { Column } from 'components';
import { ListItem } from './ListItem';
import { ITodo } from 'interfaces';

type ListProps = {
    items: ITodo[];
    selectedIndex: number;
    onClick: (index: number) => void;
  };

export const List: React.FC<ListProps> = ({ items, selectedIndex, onClick }) => {
    return (
        <Column py="10px">
            {items.map((item, index) => (
              <ListItem key={index} {...item} isActive={index === selectedIndex} index={index} onClick={onClick} />
            ))}
        </Column>
    );
};

