import { Column, Row, Text, Icon } from 'components';

export type ListItemProps = {
    index: number;
    id: string;
    task: string;
    isDone: number;
    onClick: (index: number) => void;
    isActive: boolean;
  };

export const ListItem: React.FC<ListItemProps> = ({ index, id, task, isDone, isActive, onClick }) => {
    return (
